function Keys(){
	// private variables
	var keys = [{"key":"C","signature":"0","major_chords":[{"tonic":[{"chord":"C","modf":"major"}],"sub_dom":[{"chord":"F","modf":"major"}],"dom":[{"chord":"G","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"A","modf":"minor"}],"1":[{"chord":"D","modf":"minor"}],"2":[{"chord":"E","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"D","modf":"7"}],"tri_dom":[{"chord":"A","modf":"7"}],"dom_rel_minor":"E7"}]},{"key":"G","signature":"1#","major_chords":[{"tonic":[{"chord":"G","modf":"major"}],"sub_dom":[{"chord":"C","modf":"major"}],"dom":[{"chord":"D","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"E","modf":"minor"}],"1":[{"chord":"A","modf":"minor"}],"2":[{"chord":"B","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"A","modf":"7"}],"tri_dom":[{"chord":"E","modf":"7"}],"dom_rel_minor":[{"chord":"B","modf":"7"}]}]},{"key":"D","signature":"2#","major_chords":[{"tonic":[{"chord":"D","modf":"major"}],"sub_dom":[{"chord":"G","chord":"major"}],"dom":[{"chord":"A","chord":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"B","modf":"minor"}],"1":[{"chord":"E","modf":"minor"}],"2":[{"chord":"F#","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"E","modf":"7"}],"tri_dom":[{"chord":"B","modf":"7"}],"dom_rel_minor":[{"chord":"F#","modf":"7"}]}]},{"key":"A","signature":"3#","major_chords":[{"tonic":[{"chord":"A","modf":"major"}],"sub_dom":[{"chord":"D","modf":"major"}],"dom":[{"chord":"E","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"F#","modf":"minor"}],"1":[{"chord":"B","modf":"minor"}],"2":[{"chord":"C#","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"B","modf":"7"}],"tri_dom":[{"chord":"F#","modf":"7"}],"dom_rel_minor":[{"chord":"C#","modf":"7"}]}]},{"key":"E","signature":"4#","major_chords":[{"tonic":[{"chord":"E","modf":"major"}],"sub_dom":[{"chord":"A","modf":"major"}],"dom":[{"chord":"B","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"C#","modf":"minor"}],"1":[{"chord":"F#","modf":"minor"}],"2":[{"chord":"G#","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"F#","modf":"7"}],"tri_dom":[{"chord":"C#","modf":"7"}],"dom_rel_minor":[{"chord":"Ab","modf":"7"}]}]},{"key":"B","signature":"5#","major_chords":[{"tonic":[{"chord":"B","modf":"major"}],"sub_dom":[{"chord":"E","modf":"major"}],"dom":[{"chord":"F#","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"G#","modf":"minor"}],"1":[{"chord":"C#","modf":"minor"}],"2":[{"chord":"D#","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"C#","modf":"7"}],"tri_dom":[{"chord":"Ab","modf":"7"}],"dom_rel_minor":[{"chord":"Eb","modf":"7"}]}]},{"key":"F#","signature":"6#(b)","major_chords":[{"tonic":[{"chord":"F#","modf":"major"}],"sub_dom":[{"chord":"B","modf":"major"}],"dom":[{"chord":"C#","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"D#","modf":"minor"}],"1":[{"chord":"G#","modf":"minor"}],"2":[{"chord":"A#","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"Ab","modf":"7"}],"tri_dom":[{"chord":"Eb","modf":"7"}],"dom_rel_minor":[{"chord":"Bb","modf":"7"}]}]},{"key":"Db","signature":"5b","major_chords":[{"tonic":[{"chord":"Db","modf":"major"}],"sub_dom":[{"chord":"Gb","modf":"major"}],"dom":[{"chord":"Ab","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"Bb","modf":"minor"}],"1":[{"chord":"Eb","modf":"major"}],"2":[{"chord":"F","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"Eb","modf":"7"}],"tri_dom":[{"chord":"Bb","modf":"7"}],"dom_rel_minor":[{"chord":"F","modf":"7"}]}]},{"key":"Ab","signature":"4b","major_chords":[{"tonic":[{"chord":"Ab","modf":"major"}],"sub_dom":[{"chord":"Db","modf":"major"}],"dom":[{"chord":"Eb","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"F","modf":"minor"}],"1":[{"chord":"Bb","modf":"minor"}],"2":[{"chord":"C","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"Bb","modf":"7"}],"tri_dom":[{"chord":"F","modf":"7"}],"dom_rel_minor":[{"chord":"C","modf":"7"}]}]},{"key":"Eb","signature":"3b","major_chords":[{"tonic":[{"chord":"Eb","modf":"major"}],"sub_dom":[{"chord":"Ab","modf":"major"}],"dom":[{"chord":"Bb","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"C","modf":"minor"}],"1":[{"chord":"F","modf":"minor"}],"2":[{"chord":"G","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"F","modf":"7"}],"tri_dom":[{"chord":"C","modf":"7"}],"dom_rel_minor":[{"chord":"G","modf":"7"}]}]},{"key":"Bb","signature":"2b","major_chords":[{"tonic":[{"chord":"Bb","modf":"major"}],"sub_dom":[{"chord":"Eb","modf":"major"}],"dom":[{"chord":"F","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"G","modf":"minor"}],"1":[{"chord":"C","modf":"minor"}],"2":[{"chord":"D","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"C","modf":"7"}],"tri_dom":[{"chord":"G","modf":"7"}],"dom_rel_minor":[{"chord":"D","modf":"7"}]}]},{"key":"F","signature":"1b","major_chords":[{"tonic":[{"chord":"F","modf":"major"}],"sub_dom":[{"chord":"Bb","modf":"major"}],"dom":[{"chord":"C","modf":"7"}]}],"relative_minor_chords":[{"0":[{"chord":"D","modf":"minor"}],"1":[{"chord":"G","modf":"minor"}],"2":[{"chord":"A","modf":"7"}]}],"accidental_chords":[{"dbl_dom":[{"chord":"G","modf":"7"}],"tri_dom":[{"chord":"D","modf":"7"}],"dom_rel_minor":[{"chord":"A","modf":"7"}]}]}];
	
	var strings = [{"e":"1","a":"2","d":"3","g":"4","b":"5","e2":"6"}];

	//get a reference to the canvas
	var canvas = $('.fretboard canvas');
	var ctx = $(canvas)[0].getContext("2d");

	// fretboard and finger indicators
	finger = function(x, y, color) {
		ctx.fillStyle = color;
		ctx.beginPath();
		ctx.arc(x, y, canvas.height()*.04, 0, Math.PI*2, false); 
		ctx.closePath();
		ctx.fill();
	}
	guitarString = function(x, w, color) {
		ctx.lineWidth = w;
		ctx.beginPath();
	    ctx.moveTo(x,0);
	    ctx.lineTo(x,canvas.height());
		ctx.strokeStyle = color;
	    ctx.stroke();
	}
	fret = function(y, w, color) {
		ctx.lineWidth = w;
		ctx.beginPath();
	    ctx.moveTo(0,y);
	    ctx.lineTo(canvas.width()*2,y);
		ctx.strokeStyle = color;
	    ctx.stroke();
	}
	fingerY = function(i) {
		return ((canvas.height()/8) * i) - ((canvas.height()/8)/2)
	}
	
	// build out the fretboard
	buildFrets = function(k) {
		// Get api placement
		
		$('.fretboard').show();
		canvas.attr('height', $('.fretboard').height());
		canvas.attr('width', $('.fretboard').width());
		var fretHeight = canvas.height()/8;
		var stringSpace = canvas.width()/7;
		// print canvas
		// base 0
		//fret(0, canvas.width()*.1);
		
		// Create horizontal lines
		for (var i=1; i < 8; i++) {
			 fret(fretHeight*i, 1,"#ccc");
		};
		// Create vertical lines
		for (var i=1; i < 7; i++) {
			 guitarString(stringSpace*i, 4-i,"#666");
		};
		//loop through chords
		var api_url = 'http://pargitaru.co.cc/api/?request=chords&chord='+escape(k.key);
		console.log(api_url);
		$.get(api_url, function(data) {
			console.log(data.chords[0]);
			$.each(data.chords[0], function(i, v) { 
				console.log(i);
				console.log(strings[0][i]);
				console.log(v);
				if(v!=0) {
					finger(stringSpace*strings[0][i], fingerY(v),"#09f");
				}		
				
			});
			
		},'jsonp');
		
	}
	getSignature = function(id) {
		return keys[id].signature;
	}
	
	return {
		init: function(){
			
			// create keys
			$.each(keys, function(i, v) { 
				$('#key ul').append('<li rel="'+i+'">'+v.key+'</li>');
			});
			// live event for keys
			$('#key ul li').click(function() {
				$('#key').addClass('selected');
				$('#key ul li').removeClass('active');
				$(this).addClass('active');
				id = $(this).attr('rel');
				// Build Grid 
				buildFrets(keys[id]);
				// assign rel as ref to resize canvas
				canvas.attr('rel', id);
				
				/*
				console.log('Key ID: '+id);
				console.log('Signature: '+getSignature(id));
				console.log($('.fretboard').width());
				console.log(canvas.width());
				console.log(canvas.attr('rel'));
				*/
			});
			
			window.onresize = function() {
				// resize canvas
				// if canvas.rel
				if (canvas.attr('rel')) {
					// build grid with canvas.rel
					buildFrets(keys[canvas.attr('rel')]);
				};
			}

		}
		
	}
	
}

// Object
var keys = new Keys();
keys.init();

